from collections import defaultdict

class Solution:
    def findLength(self, nums1: list[int], nums2: list[int]) -> int:
        dic = defaultdict(int)
        
        length1 = len(nums1)
        length2 = len(nums2)
        
        for i in range(length1):
            for j in range(i, length1):
                key = tuple(nums1[i:j+1])
                dic[key] = 1
        
        print(dic)
        
        max_length = 0
        
        for i in range(length2):
            for j in range(i, length2):
                key = tuple(nums2[i:j+1])
                if dic[key] == 1:
                    max_length = max(max_length, len(key))

        return max_length
            
sol = Solution()
kq = sol.findLength([1,2,3,2,1], [3,2,1,4,7])
print(kq)


